mapper.select:
select
  id, name, user_id, version, version_message, serial_number, project_id, upload_time, version_flag, code_line_number, delete_flag
from project_management_cloud.document


        and id = #{id}


        and name = #{name}


        and user_id = #{userId}


        and version = #{version}


        and version_message = #{versionMessage}


        and serial_number = #{serialNumber}


        and project_id = #{projectId}


        and upload_time = #{uploadTime}


        and version_flag = #{versionFlag}


        and code_line_number = #{codeLineNumber}


        and delete_flag = #{deleteFlag}
