mapper.select:
select
  id, project_id, package_id, document_name, package_name, create_time, user_id, delete_flag
from project_management_cloud.project_package


        and id = #{id}


        and project_id = #{projectId}


        and package_id = #{packageId}


        and document_name = #{documentName}


        and package_name = #{packageName}


        and create_time = #{createTime}


        and user_id = #{userId}


        and delete_flag = #{deleteFlag}
