mapper.select:
select
  id, project_id, from_user_id, to_user_id, type_id, message, time, is_read, delete_flag, all_flag, admin_flag, need_to_do
from project_management_project_cloud.project_message


        and id = #{id}


        and project_id = #{projectId}


        and from_user_id = #{fromUserId}


        and to_user_id = #{toUserId}


        and type_id = #{typeId}


        and message = #{message}


        and time = #{time}


        and is_read = #{isRead}


        and delete_flag = #{deleteFlag}


        and all_flag = #{allFlag}


        and admin_flag = #{adminFlag}


        and need_to_do = #{needToDo}
